import React from 'react'

import about_image from '../../image/logo-black-outline.png'
import about_text_image from '../../image/about-text-image.png'

import './about.css'







const About = () => {

  function mouseOver(event) {
    let element = document.getElementById(event.target.id);
    element.style.transform = 'scale(1.10)';
  }
  
  function mouseLeave(event) {
    let element = document.getElementById(event.target.id);
    element.style.transform = 'scale(1.0)';
  }

  return (
    <div className='about'>
      <div className='aboutLeftContainer'>
        <div className='aboutImageContainer'>
          <img data-aos="flip-left" src={about_image} alt='' className='aboutImage' id='aboutImage' onMouseOver={mouseOver} onMouseLeave={mouseLeave}/>
        </div>
      </div>
      <div className='aboutRightContainer'>
        <div className='aboutTextContainer'>
          <div className='aboutText aboutTitle aboutTextTop' data-aos="fade-down" data-aos-delay={1 * delay_gap}>Realize yourself with <span style={{ color: 'var(--color-main)' }}>astrology & AI</span>.</div>
          <div className='aboutText aboutTitle' data-aos="fade-down-left" data-aos-delay={2 * delay_gap}>Explore the stars & <span style={{ color: 'var(--color-main)' }}>earn rewards.</span></div>
          <div className='aboutText aboutTitle' data-aos="fade-left" data-aos-delay={3 * delay_gap}>Forge your unique <span style={{ color: 'var(--color-main)' }}>Alchm Avatar.</span></div>
          <div  className='aboutText aboutTitle' data-aos="fade-up-left" data-aos-delay={4 * delay_gap}>Own your data. Own your identity.<br /><span style={{ color: 'var(--color-main)' }}>Own your earnings.</span></div>
          <div className='aboutText aboutTitle aboutTextBottom' data-aos="fade-up" data-aos-delay={5 * delay_gap}><span style={{ color: 'var(--color-main)' }}>Discover your Alchemy.</span></div>
        </div>
      </div>
    </div>
  )
}

export default About
